-- ============================================
-- 金融行业常见问题（50条）
-- ============================================

INSERT INTO industry_question_library (industry, question, answer, category, importance, tags, source_type) VALUES

-- 开户服务类（10条）
('金融', '如何开通网上银行？', '您可以通过以下三种方式开通网上银行：1. 通过我行官网自助注册（需要银行卡和身份证）；2. 携带身份证和银行卡到任意网点柜台办理；3. 通过手机银行APP在线开通。开通后即可享受24小时在线转账、理财、缴费等服务。', '开户服务', 'high', '网上银行,开户,注册', 'preset'),
('金融', '开户需要准备什么材料？', '个人开户需要准备：1. 有效身份证原件；2. 本人手机号码；3. 预留资金（一般100元起）。企业开户需要：1. 营业执照原件及复印件；2. 法人身份证；3. 公章、财务章、法人章；4. 经办人身份证。', '开户服务', 'high', '开户,材料,证件', 'preset'),
('金融', '网上银行和手机银行有什么区别？', '网上银行是通过电脑浏览器访问，适合大额转账和复杂操作；手机银行是通过手机APP使用，更便捷，支持扫码支付、指纹登录。建议两者都开通，使用更灵活。功能上基本一致，都支持转账、理财、贷款申请等。', '开户服务', 'medium', '网上银行,手机银行,区别', 'preset'),
('金融', '开户后多久可以使用？', '柜台开户当场即可使用，拿到银行卡后立即可以存取款。网上银行和手机银行开通后需要设置密码，约5-10分钟即可使用。大额转账功能可能需要次日生效，小额转账立即可用。', '开户服务', 'medium', '开户,生效时间', 'preset'),
('金融', '可以代办银行卡吗？', '一般情况下银行卡需要本人携带身份证到网点办理，不支持代办。特殊情况（如行动不便）可以申请上门服务，需要提前预约，并提供相关证明材料。未成年人开户需监护人陪同。', '开户服务', 'medium', '代办,银行卡', 'preset'),
('金融', '一个人可以开几张银行卡？', '同一银行同一客户名下，I类账户（全功能账户）只能开立一个，II类和III类账户可开多个。不同银行可分别开立。建议合理规划，避免账户过多难以管理。', '开户服务', 'low', '银行卡数量,账户类型', 'preset'),
('金融', '学生可以办理银行卡吗？', '可以。16周岁以上学生可凭身份证和学生证独立办理；16周岁以下需监护人陪同，提供户口本或出生证明。学生卡通常免年费、小额账户管理费，享受优惠政策。', '开户服务', 'medium', '学生,银行卡,未成年', 'preset'),
('金融', '银行卡丢了怎么办？', '立即拨打银行客服电话95XXX进行口头挂失，然后尽快携带身份证到网点办理正式挂失和补卡。口头挂失有效期5天，正式挂失后7个工作日内可领取新卡。挂失期间账户被冻结，确保资金安全。', '开户服务', 'high', '银行卡,丢失,挂失', 'preset'),
('金融', '如何修改预留手机号？', '携带身份证和银行卡到任意网点柜台办理，或通过网上银行、手机银行自助修改（需要短信验证）。修改后立即生效，新手机号将接收所有银行通知和验证码。', '开户服务', 'medium', '手机号,修改', 'preset'),
('金融', '银行卡年费是多少？', '借记卡年费一般10元/年，部分银行首张卡免年费。信用卡年费根据卡等级不同，普卡100-200元/年，金卡300-600元/年，白金卡1000-3000元/年。多数信用卡刷满一定次数可免年费。', '开户服务', 'medium', '年费,收费标准', 'preset'),

-- 转账汇款类（10条）
('金融', '转账手续费怎么收？', '本行同城转账免费；跨行转账：2000元以下2元，2000-5000元5元，5000-10000元10元，1万-5万元15元，5万以上按0.03%收取，最高50元。手机银行转账多数免手续费。', '转账汇款', 'high', '转账,手续费,收费', 'preset'),
('金融', '转账多久到账？', '本行转账实时到账；跨行转账：普通模式2小时内，加急模式实时到账，次日模式次日到账。大额转账（5万以上）需要人工审核，到账时间可能延长。节假日到账时间可能顺延。', '转账汇款', 'high', '转账,到账时间', 'preset'),
('金融', '转账限额是多少？', '手机银行：单笔限额5万，日累计20万；网上银行：单笔限额100万，日累计500万；柜台转账无限额。可根据需要申请调整限额，需本人携带身份证和银行卡到网点办理。', '转账汇款', 'high', '转账,限额', 'preset'),
('金融', '转账转错了怎么办？', '立即联系银行客服申请撤回（仅限未到账状态）；若已到账，需联系收款方协商退回，或通过法律途径追回。建议转账前仔细核对姓名和账号，使用"确认收款人姓名"功能。', '转账汇款', 'high', '转账,转错,追回', 'preset'),
('金融', '可以向境外转账吗？', '可以办理境外汇款业务。需要提供：1. 收款人姓名、账号、银行SWIFT代码；2. 本人身份证；3. 汇款用途证明。手续费一般0.1%，最低100元，最高300元，另加电报费150元。到账时间1-3个工作日。', '转账汇款', 'medium', '境外汇款,国际转账', 'preset'),
('金融', '什么是实时转账、普通转账、次日转账？', '实时转账：立即到账，适合急用；普通转账：2小时内到账，可撤回；次日转账：24小时后到账，防诈骗。建议陌生人转账选择次日到账，发现问题可及时撤回。', '转账汇款', 'medium', '转账类型,到账模式', 'preset'),
('金融', '转账为什么需要输入对方姓名？', '这是央行为防范电信诈骗推出的"确认收款人姓名"功能。如果输入姓名与账号不匹配，系统会提示错误，避免转错账或被骗。建议每次转账都认真核对。', '转账汇款', 'medium', '转账,安全,验证', 'preset'),
('金融', '如何设置转账限额？', '登录手机银行或网上银行，进入"安全中心-限额管理"，可自行设置单笔和单日转账限额。调高限额需要身份验证，部分银行需要到网点办理。建议根据实际需求合理设置。', '转账汇款', 'low', '转账,限额,设置', 'preset'),
('金融', '大额转账需要预约吗？', '5万元以上现金支取需要提前1天预约；转账不需要预约，但大额转账（50万以上）可能需要人工审核，建议提前咨询。对公大额转账建议提前与客户经理沟通。', '转账汇款', 'medium', '大额转账,预约', 'preset'),
('金融', '转账备注有什么用？', '转账备注可以标注汇款用途，方便收款方识别款项来源，也便于自己记账对账。企业转账必须填写备注，个人转账可选填。备注内容不要包含敏感信息。', '转账汇款', 'low', '转账,备注,用途', 'preset'),

-- 信用卡服务类（10条）
('金融', '如何申请信用卡？', '申请方式：1. 网上申请（官网/手机APP）；2. 网点柜台申请；3. 业务员上门办理。需要提供身份证、工作证明、收入证明。审批时间7-15个工作日，通过后邮寄到家。首次办卡建议选择普卡，额度一般3000-10000元。', '信用卡服务', 'high', '信用卡,申请,办卡', 'preset'),
('金融', '信用卡额度如何提升？', '提升额度需满足：1. 持卡满6个月；2. 按时还款无逾期；3. 经常使用信用卡消费；4. 收入证明提升。可通过手机银行APP或致电客服申请，也可以提供资产证明（房产、车辆、存款）快速提额。临时额度可随时申请。', '信用卡服务', 'high', '信用卡,额度,提额', 'preset'),
('金融', '信用卡还款日期怎么算？', '账单日后的20天为还款日。例如：账单日每月5日，还款日为每月25日。账单日到还款日之间为免息期，最长可达50天。建议设置自动还款，避免逾期。可申请修改账单日，一年可改一次。', '信用卡服务', 'high', '信用卡,还款日,账单日', 'preset'),
('金融', '最低还款是什么意思？', '最低还款额一般是账单金额的10%，按最低还款不算逾期，但剩余未还部分从消费日开始计息，日息万分之五，年化约18%。建议尽量全额还款，避免高额利息。', '信用卡服务', 'high', '信用卡,最低还款,利息', 'preset'),
('金融', '信用卡取现要手续费吗？', '信用卡取现收费：1. 手续费：取现金额的1-3%，最低10元；2. 利息：从取现日起每天万分之五，无免息期。建议紧急情况才取现，日常用借记卡取现。取现额度一般为信用额度的50%。', '信用卡服务', 'medium', '信用卡,取现,手续费', 'preset'),
('金融', '信用卡逾期有什么后果？', '逾期影响：1. 产生滞纳金（最低还款额的5%）；2. 产生罚息（日息万分之五）；3. 影响个人征信，5年内无法消除；4. 严重逾期可能被起诉。建议设置还款提醒，确保按时还款。', '信用卡服务', 'high', '信用卡,逾期,征信', 'preset'),
('金融', '信用卡可以分期吗？', '支持分期业务：1. 账单分期（3/6/9/12/24期）；2. 单笔消费分期；3. 现金分期。手续费根据期数不同，一般0.6-0.75%/月。大额消费建议分期，减轻还款压力。提前还款需支付剩余所有手续费。', '信用卡服务', 'medium', '信用卡,分期,手续费', 'preset'),
('金融', '信用卡激活后不用会扣费吗？', '信用卡激活后，即使不使用也会产生年费（首年多数免费）。长期不用建议注销，避免忘记年费产生欠款影响征信。部分卡种刷够次数可免年费，具体以卡片说明为准。', '信用卡服务', 'medium', '信用卡,年费,不使用', 'preset'),
('金融', '如何注销信用卡？', '注销流程：1. 还清所有欠款（包括年费）；2. 确认无未入账交易；3. 致电客服申请注销或到网点办理；4. 45天后正式注销。注销前建议查询积分是否兑换，注销后积分清零。', '信用卡服务', 'medium', '信用卡,注销,销卡', 'preset'),
('金融', '信用卡丢了怎么办？', '立即致电客服挂失（24小时热线），挂失后立即生效，避免盗刷。然后申请补卡，补卡费一般20-50元，7-15天寄到。挂失前48小时的盗刷损失，多数银行提供失卡保障服务。', '信用卡服务', 'high', '信用卡,丢失,挂失', 'preset'),

-- 贷款服务类（10条）
('金融', '如何办理个人贷款？', '办理流程：1. 提交申请（网点/手机APP）；2. 提供资料（身份证、收入证明、征信报告、抵押物证明）；3. 银行审批（3-7个工作日）；4. 签订合同；5. 放款（1-3个工作日）。贷款种类：消费贷、经营贷、房贷、车贷等。', '贷款服务', 'high', '贷款,申请,流程', 'preset'),
('金融', '贷款利率是多少？', '贷款利率根据类型不同：1. 房贷：LPR+基点，目前约4-5%；2. 车贷：3-8%；3. 消费贷：6-12%；4. 经营贷：4-6%；5. 信用贷：8-15%。具体利率根据个人资质、贷款金额、期限浮动。', '贷款服务', 'high', '贷款,利率,年利率', 'preset'),
('金融', '贷款需要什么条件？', '基本条件：1. 年满18周岁，具有完全民事行为能力；2. 有稳定工作和收入；3. 个人征信良好，无逾期记录；4. 负债率不超过50%；5. 提供抵押物或担保（部分贷款）。具体条件因贷款类型而异。', '贷款服务', 'high', '贷款,条件,资格', 'preset'),
('金融', '等额本息和等额本金有什么区别？', '等额本息：每月还款额固定，前期利息多本金少，适合收入稳定者。等额本金：每月本金固定，利息递减，总利息更少，但前期还款压力大。建议：长期贷款选等额本金更省钱，短期或收入不高选等额本息。', '贷款服务', 'medium', '贷款,还款方式,等额本息', 'preset'),
('金融', '可以提前还贷吗？', '可以提前还贷，分为部分提前还款和全部提前还款。注意：1. 部分银行要求贷款满1年才能提前还；2. 可能收取违约金（贷款金额的1-3%）；3. 需提前预约，携带身份证、贷款合同到网点办理。建议咨询客户经理。', '贷款服务', 'medium', '贷款,提前还款,违约金', 'preset'),
('金融', '贷款审批需要多久？', '审批时间：1. 信用贷款：1-3个工作日；2. 抵押贷款：5-7个工作日；3. 房贷：7-15个工作日；4. 经营贷：3-7个工作日。审批通过后1-3个工作日放款。节假日顺延，资料齐全可加快审批。', '贷款服务', 'medium', '贷款,审批,时间', 'preset'),
('金融', '征信不好能贷款吗？', '征信不良影响贷款：1. 逾期1-2次，影响较小，可能提高利率；2. 连续逾期3次或累计逾期6次，很难批贷；3. 当前有逾期，无法贷款。建议：保持良好还款记录，征信报告5年更新一次。', '贷款服务', 'high', '贷款,征信,逾期', 'preset'),
('金融', '贷款额度是怎么计算的？', '额度计算因素：1. 收入水平（月还款不超过月收入50%）；2. 抵押物价值（房贷最高70%，车贷最高80%）；3. 征信情况；4. 现有负债。例如：月收入1万，最高月供5000元，贷30年最高可贷约120万（利率5%）。', '贷款服务', 'medium', '贷款,额度,计算', 'preset'),
('金融', '贷款逾期了怎么办？', '逾期应对：1. 立即还款，减少逾期天数；2. 主动联系银行说明情况；3. 申请延期还款或分期还款；4. 严重逾期可申请停息挂账。逾期后果：产生罚息、影响征信、可能被起诉、抵押物被拍卖。', '贷款服务', 'high', '贷款,逾期,后果', 'preset'),
('金融', '可以用公积金贷款吗？', '公积金贷款条件：1. 连续缴存公积金6-12个月；2. 账户状态正常；3. 购买首套或二套住房。优势：利率低（3.1-3.575%），比商贷低1-2%。缺点：额度有限（30-120万），放款慢。可组合商贷使用。', '贷款服务', 'medium', '公积金贷款,利率', 'preset'),

-- 理财投资类（10条）
('金融', '有哪些理财产品可以选择？', '理财产品类型：1. 银行存款（活期、定期）：安全，收益低1-3%；2. 货币基金：灵活，收益2-3%；3. 理财产品：稳健，收益3-5%；4. 基金：中高风险，收益5-15%；5. 股票：高风险，收益不确定。建议根据风险承受能力选择。', '理财投资', 'high', '理财,产品,投资', 'preset'),
('金融', '理财产品安全吗？', '理财产品风险等级：R1（低风险）、R2（中低）、R3（中）、R4（中高）、R5（高）。银行理财已打破刚性兑付，不保本。建议：1. 选择R1-R2级别产品；2. 不要把所有钱投入一种产品；3. 认真阅读产品说明书。', '理财投资', 'high', '理财,安全,风险', 'preset'),
('金融', '定期存款利率是多少？', '定期存款利率（2025年参考）：3个月1.25%，6个月1.45%，1年1.65%，2年1.95%，3年2.20%，5年2.25%。大额存单利率更高，20万起存，上浮40-50%。建议根据资金使用计划选择期限。', '理财投资', 'medium', '定期存款,利率', 'preset'),
('金融', '购买基金如何选择？', '基金选择建议：1. 新手选货币基金或债券基金（低风险）；2. 看基金历史业绩（3-5年）；3. 看基金经理经验；4. 看基金规模（5-50亿为宜）；5. 分散投资，不要all in。长期持有收益更稳定，频繁买卖成本高。', '理财投资', 'medium', '基金,投资,选择', 'preset'),
('金融', '基金什么时候买入最好？', '基金买入策略：1. 定投：每月固定时间买入固定金额，平摊成本；2. 逢低买入：市场下跌时分批建仓；3. 不要追涨杀跌。建议：长期定投指数基金，3-5年收益较稳定。市场大跌是入场好时机。', '理财投资', 'medium', '基金,买入,定投', 'preset'),
('金融', '理财产品可以提前赎回吗？', '理财产品赎回规则：1. 开放式产品：可随时赎回，T+1到账；2. 封闭式产品：不可提前赎回；3. 定期开放产品：开放期可赎回。提前赎回可能损失收益或收取手续费。建议购买前确认资金使用时间。', '理财投资', 'medium', '理财,赎回,提现', 'preset'),
('金融', '如何分散投资风险？', '分散投资建议：1. 不同资产类别：股票30%、债券40%、存款30%；2. 不同行业：科技、消费、医疗、金融；3. 不同市场：A股、港股、美股；4. 不同期限：短期、中期、长期。原则：不要把鸡蛋放在一个篮子里。', '理财投资', 'high', '投资,风险,分散', 'preset'),
('金融', '收益率怎么计算？', '收益率计算：年化收益率 = (收益 / 本金) / 持有天数 × 365 × 100%。例如：投入10万元，30天后收益300元，年化收益率 = (300/100000) / 30 × 365 × 100% = 3.65%。注意：银行宣传的收益率多为年化收益率。', '理财投资', 'low', '收益率,计算,年化', 'preset'),
('金融', '理财产品费用有哪些？', '理财费用：1. 申购费（买入）：0-1.5%，打折后0.1-0.6%；2. 赎回费（卖出）：持有时间越长越低，持有2年以上多数免费；3. 管理费：年费率0.5-1.5%；4. 托管费：年费率0.1-0.25%。管理费和托管费从净值中扣除。', '理财投资', 'low', '理财,费用,手续费', 'preset'),
('金融', '如何判断理财产品好坏？', '判断标准：1. 看历史业绩（至少3年）；2. 看最大回撤（下跌幅度，越小越好）；3. 看夏普比率（收益风险比，越高越好）；4. 看基金经理从业年限和过往业绩；5. 看基金公司实力和口碑。不要只看短期收益。', '理财投资', 'medium', '理财,选择,评估', 'preset');

-- ============================================
-- 医疗行业常见问题（50条）
-- ============================================

INSERT INTO industry_question_library (industry, question, answer, category, importance, tags, source_type) VALUES

-- 就医指南类（10条）
('医疗', '如何预约挂号？', '预约挂号方式：1. 微信公众号在线预约（推荐）；2. 支付宝生活号预约；3. 医院官方APP预约；4. 拨打预约电话114或医院电话；5. 现场自助机挂号；6. 窗口挂号。建议提前1-7天预约，热门专家号需要提前更久。', '就医指南', 'high', '挂号,预约,就诊', 'preset'),
('医疗', '初诊和复诊有什么区别？', '初诊：第一次就诊该疾病，需详细询问病史、做全面检查，时间较长，费用较高。复诊：已确诊疾病的后续诊疗，主要看检查结果、调整方案，时间短，费用低。慢性病建议挂同一医生复诊，便于连续治疗。', '就医指南', 'medium', '初诊,复诊,就诊', 'preset'),
('医疗', '检查报告多久能出来？', '检查出报告时间：1. 血常规、尿常规：2-4小时；2. 生化检查：当天或次日；3. X光、CT：2-4小时；4. 核磁共振MRI：1-2天；5. 病理检查：3-7天；6. 基因检测：1-2周。急诊检查可加急，1小时内出结果。', '就医指南', 'medium', '检查报告,时间', 'preset'),
('医疗', '可以直接看专家号吗？', '可以直接预约专家号，但建议：1. 复杂疑难病症看专家；2. 普通感冒发烧看普通号即可；3. 慢性病首次确诊看专家，后续复诊看普通号。专家号费用较高（50-500元），且更难预约。', '就医指南', 'medium', '专家号,挂号', 'preset'),
('医疗', '忘记带就诊卡怎么办？', '解决方法：1. 提供身份证或医保卡，系统可查询就诊卡号；2. 手机APP/微信公众号绑定电子就诊卡；3. 现场补办实体卡（费用5-10元）；4. 使用医保电子凭证（推荐）。电子就诊卡与实体卡同等效力。', '就医指南', 'medium', '就诊卡,忘记', 'preset'),
('医疗', '急诊和门诊有什么区别？', '急诊：24小时接诊，处理急危重症（如胸痛、呼吸困难、大出血、骨折等），无需预约，费用较高。门诊：工作时间接诊（8:00-17:00），处理常见病、慢性病，需预约挂号。建议：非紧急情况挂门诊，节省时间和费用。', '就医指南', 'high', '急诊,门诊,区别', 'preset'),
('医疗', '检查前需要空腹吗？', '需要空腹的检查：1. 抽血化验（肝功、血脂、血糖）：空腹8-12小时；2. 腹部B超：空腹6-8小时；3. 胃镜：空腹8小时。不需要空腹：血常规、尿常规、心电图、X光、CT（非增强）。具体以医嘱为准。', '就医指南', 'medium', '检查,空腹,注意事项', 'preset'),
('医疗', '如何查看检查结果？', '查询检查结果方法：1. 医院官方APP或微信公众号查询（推荐）；2. 自助机打印报告；3. 窗口领取纸质报告；4. 电话查询。检验报告通常当天可查，影像报告次日可查。建议保存电子版，复诊时方便调阅。', '就医指南', 'medium', '检查结果,查询', 'preset'),
('医疗', '看病需要带什么材料？', '就诊必备材料：1. 身份证或医保卡；2. 既往病历和检查报告（复诊）；3. 正在服用的药品清单；4. 过敏史记录。首次就诊建议携带近期体检报告，方便医生了解整体健康状况。', '就医指南', 'medium', '就诊,材料,准备', 'preset'),
('医疗', '可以代替家人挂号吗？', '可以代挂号，需提供患者身份证号和手机号。但就诊时必须本人到场（儿童需监护人陪同，行动不便老人可代办）。代取药需要：1. 患者身份证；2. 处方单；3. 代办人身份证。', '就医指南', 'low', '代挂号,代办', 'preset'),

-- 医保服务类（10条）
('医疗', '医保如何报销？', '医保报销流程：1. 就诊时出示医保卡或医保电子凭证；2. 系统自动识别医保范围；3. 结算时自动扣除医保报销部分；4. 个人只需支付自费部分。异地就医需提前备案。报销比例：门诊50-70%，住院70-90%。', '医保服务', 'high', '医保,报销,流程', 'preset'),
('医疗', '哪些项目医保不能报销？', '医保不报销项目：1. 美容整形；2. 健康体检；3. 预防接种；4. 近视眼矫正；5. 生活用品（保健品、营养品）；6. 特需医疗、VIP病房差价；7. 境外就医；8. 工伤、交通事故（由责任方承担）。', '医保服务', 'high', '医保,报销范围', 'preset'),
('医疗', '医保卡余额怎么查？', '医保余额查询方法：1. 微信-支付-医疗健康-医保电子凭证；2. 支付宝-市民中心-医保；3. 各地医保APP；4. 医院自助机查询；5. 定点药店刷卡查询；6. 拨打12333咨询。建议绑定医保电子凭证，随时查询。', '医保服务', 'medium', '医保,余额,查询', 'preset'),
('医疗', '异地就医如何报销？', '异地就医报销流程：1. 提前在参保地医保局备案（线上或线下）；2. 选择就医地的异地定点医疗机构；3. 持医保卡直接结算，无需垫付。未备案报销比例降低10-20%。急诊可事后备案。建议长期异地居住办理长期备案。', '医保服务', 'high', '医保,异地就医,备案', 'preset'),
('医疗', '医保卡可以给家人用吗？', '医保个人账户余额可以给家人使用（仅限看病买药），但统筹账户（住院报销）不能共用。使用方法：1. 医保卡绑定家庭成员；2. 去定点医院/药店时出示本人医保卡。注意：不能套现，违规使用会被处罚。', '医保服务', 'medium', '医保,家人共用', 'preset'),
('医疗', '医保报销比例是多少？', '医保报销比例（以职工医保为例）：门诊：社区医院90%，二级医院70%，三级医院50%；住院：起付线以上部分85-95%。城乡居民医保比例更低。不同地区略有差异，大病医疗可二次报销，最高报销90%以上。', '医保服务', 'high', '医保,报销比例', 'preset'),
('医疗', '什么是医保起付线和封顶线？', '起付线：报销门槛，低于起付线全部自费。例如住院起付线1000元，花费800元全自费，花费2000元报销(2000-1000)×报销比例。封顶线：年度报销上限，超出部分自费。职工医保封顶线一般30-50万，大病保险可提高至100万。', '医保服务', 'medium', '医保,起付线,封顶线', 'preset'),
('医疗', '医保断缴了还能用吗？', '医保断缴影响：1. 次月起无法使用医保报销；2. 个人账户余额仍可使用；3. 3个月内续缴，待遇连续；4. 超过3个月，重新计算连续缴费年限。建议：离职期间挂靠单位或以灵活就业人员身份续缴，避免断缴影响报销和退休待遇。', '医保服务', 'high', '医保,断缴,影响', 'preset'),
('医疗', '如何激活医保电子凭证？', '激活医保电子凭证步骤：1. 微信搜索"医保电子凭证"或支付宝搜索"医保码"；2. 进行人脸识别认证；3. 设置密码；4. 激活成功。优势：无需携带实体卡，手机扫码即可就医购药。全国通用，支持异地就医。', '医保服务', 'medium', '医保电子凭证,激活', 'preset'),
('医疗', '生育保险如何报销？', '生育保险报销：1. 连续缴纳生育保险12个月；2. 符合计划生育政策；3. 产检费用：累计报销1000-2000元；4. 生产费用：顺产3000-5000元，剖宫产5000-8000元；5. 生育津贴：产假期间按月工资发放。男职工生育保险可给配偶使用50%。', '医保服务', 'medium', '生育保险,报销', 'preset'),

-- 住院服务类（10条）
('医疗', '住院需要准备什么？', '住院准备物品：1. 证件：身份证、医保卡、就诊卡；2. 生活用品：换洗衣物、洗漱用品、拖鞋、毛巾；3. 餐具：水杯、饭盒、筷子；4. 电子设备：手机、充电器；5. 病历资料：既往检查报告、用药清单。建议携带少量现金，押金可刷卡或手机支付。', '住院服务', 'medium', '住院,准备,物品', 'preset'),
('医疗', '住院押金需要多少？', '住院押金金额：1. 普通疾病：5000-10000元；2. 手术治疗：10000-30000元；3. 重症监护ICU：50000元起；4. 具体根据病情和治疗方案确定。押金可分次交纳，出院时多退少补。支持现金、刷卡、医保卡支付。', '住院服务', 'medium', '住院,押金,费用', 'preset'),
('医疗', '什么情况下需要住院？', '需要住院的情况：1. 需要手术治疗；2. 病情严重需要持续观察和治疗；3. 需要输液超过3天；4. 需要特殊检查和治疗；5. 医生评估门诊无法处理。住院优势：医保报销比例高，医疗监护更全面。轻症建议门诊治疗，节省费用。', '住院服务', 'medium', '住院,条件,标准', 'preset'),
('医疗', '住院期间可以出院吗？', '住院期间外出规定：1. 短暂外出（如就餐、取物）：征得护士同意，当天返回；2. 请假外出：需主管医生批准，签署知情同意书，请假期间费用和风险自负；3. 自动出院：签署自动出院声明，后果自负，可能影响医保报销。', '住院服务', 'low', '住院,外出,请假', 'preset'),
('医疗', '住院期间可以更换病房吗？', '更换病房说明：1. 同级别病房可申请更换（如三人间换三人间）；2. 升级病房需补差价（普通间→单人间，差价50-500元/天）；3. 降级病房退还差价；4. 需床位空缺，向护士站申请。医保只报销普通病房费用。', '住院服务', 'low', '住院,病房,更换', 'preset'),
('医疗', '出院手续如何办理？', '出院办理流程：1. 医生开出院医嘱；2. 护士站结清费用（押金多退少补）；3. 领取出院证明、病历摘要、发票；4. 归还医院用品（如病号服）；5. 离开病房。出院后7天内可回医院复印完整病历。出院带药一般不超过7天量。', '住院服务', 'medium', '出院,手续,流程', 'preset'),
('医疗', '住院期间谁来照顾？', '住院陪护说明：1. 普通病房允许1名家属陪护；2. 重症监护ICU不允许陪护，每天探视1-2次；3. 无家属陪护可聘请护工（费用150-300元/天）；4. 陪护家属需办理陪护证。建议家属轮流陪护，确保休息。', '住院服务', 'low', '住院,陪护,护工', 'preset'),
('医疗', '住院期间可以自己买药吗？', '住院期间用药规定：1. 原则上不能私自用药，影响治疗效果且有风险；2. 特殊药品（医院无、自费药）可向医生说明，经批准后院外购买；3. 未经批准用药发生不良反应，医院不承担责任。建议所有用药咨询医生。', '住院服务', 'low', '住院,用药,自购药', 'preset'),
('医疗', '住院费用如何结算？', '住院费用结算：1. 医保患者：系统自动计算报销部分，个人只付自费部分；2. 自费患者：支付全部费用；3. 商业保险：先垫付，后凭发票、病历、清单到保险公司报销。建议住院期间留意每日费用清单，有疑问及时询问。', '住院服务', 'medium', '住院,费用,结算', 'preset'),
('医疗', '住院期间可以转院吗？', '转院流程：1. 本院无法治疗或患者要求转院；2. 主管医生开具转院证明；3. 办理出院手续；4. 联系接收医院确认床位；5. 转院后重新办理住院。注意：医保患者转院需医生证明，否则影响报销。急危重症可先转院再补手续。', '住院服务', 'medium', '住院,转院,流程', 'preset'),

-- 用药指导类（10条）
('医疗', '药品饭前吃还是饭后吃？', '用药时间说明：饭前（空腹）：胃药、降糖药、促胃动力药，饭前30分钟；饭后：消炎药、维生素、铁剂，饭后30分钟；饭中：降糖药阿卡波糖；睡前：安眠药、钙片、降脂药。具体以药品说明书或医嘱为准。', '用药指导', 'high', '用药,时间,饭前饭后', 'preset'),
('医疗', '药品过期了还能吃吗？', '过期药品不能服用！原因：1. 药效降低甚至失效；2. 可能产生有害物质；3. 引发不良反应。建议：定期清理家庭药箱，过期药品不要随意丢弃，可送到药店或社区回收点。开封后的药品按标注有效期减半使用。', '用药指导', 'high', '过期药,用药安全', 'preset'),
('医疗', '中药和西药可以一起吃吗？', '中西药联用建议：1. 间隔2小时以上服用；2. 部分中药影响西药吸收（如含鞣酸的中药影响铁剂吸收）；3. 特殊情况需医生指导（如降压药、降糖药与中药联用）。建议：主动告知医生所有用药，避免相互作用。', '用药指导', 'medium', '中药,西药,联用', 'preset'),
('医疗', '抗生素可以自己买来吃吗？', '抗生素使用规定：1. 处方药，需医生处方才能购买；2. 滥用抗生素导致耐药性，未来感染更难治疗；3. 普通感冒病毒感染不需要抗生素；4. 必须用抗生素时，需按疗程服用（一般5-7天），不能症状好转就停药。', '用药指导', 'high', '抗生素,处方药,滥用', 'preset'),
('医疗', '忘记吃药了怎么办？', '漏服药品应对：1. 短效药（每天2-3次）：想起立即补服，接近下次服药时间则跳过；2. 长效药（每天1次）：12小时内补服，超过12小时跳过；3. 降糖药、降压药：不要加倍补服；4. 避孕药：漏服超过12小时补救措施请咨询医生。', '用药指导', 'medium', '漏服,忘记吃药', 'preset'),
('医疗', '药品应该如何保存？', '药品保存方法：1. 阴凉干燥处（温度<25℃，避光、防潮）；2. 冷藏药品（如胰岛素）：2-8℃冰箱冷藏，不能冷冻；3. 口服液开封后1个月内用完；4. 眼药水开封后1个月内用完；5. 药品与包装、说明书一起保存。', '用药指导', 'medium', '药品,保存,储存', 'preset'),
('医疗', '药品不良反应怎么办？', '药品不良反应处理：1. 轻微反应（如恶心、皮疹）：停药观察，多喝水；2. 严重反应（如呼吸困难、过敏性休克）：立即停药，就近就医或拨打120；3. 及时向医生或药师报告；4. 记录不良反应药品，避免再次使用。', '用药指导', 'high', '药品,不良反应,副作用', 'preset'),
('医疗', '儿童用药有什么注意事项？', '儿童用药注意：1. 严格按体重或年龄计算剂量；2. 不要用成人药减量给儿童；3. 避免使用含阿司匹林药物（易引起瑞氏综合征）；4. 糖浆类药品用量杯或注射器量取，不要用普通勺子；5. 不要哄骗孩子说药是糖果。', '用药指导', 'high', '儿童用药,安全', 'preset'),
('医疗', '孕妇可以吃药吗？', '孕妇用药原则：1. 孕期前3个月尽量不用药（致畸高风险期）；2. 必须用药选择FDA分类A/B类药物；3. 主动告知医生怀孕情况；4. 不要自行购买非处方药；5. 中成药也不一定安全，需咨询医生。哺乳期用药也需谨慎。', '用药指导', 'high', '孕妇,用药,安全', 'preset'),
('医疗', '吃药能喝酒吗？', '药酒相互作用：1. 绝对禁止：头孢类抗生素+酒（双硫仑反应，危及生命）；2. 不建议：止痛药、降压药、降糖药、安眠药+酒（增加不良反应）；3. 服药期间和停药后7天内避免饮酒。建议：就医时告知医生饮酒情况。', '用药指导', 'high', '用药,喝酒,禁忌', 'preset'),

-- 体检服务类（10条）
('医疗', '体检需要预约吗？', '体检预约：1. 个人体检：建议提前3-7天预约（高峰期提前更久）；2. 单位体检：单位统一预约；3. 预约方式：微信公众号、医院APP、电话、现场。预约可选择合适时间，避免长时间等待。体检当天需空腹。', '体检服务', 'medium', '体检,预约', 'preset'),
('医疗', '体检前有什么注意事项？', '体检前注意事项：1. 体检前3天清淡饮食，不饮酒；2. 体检当天空腹8-12小时（可少量饮水）；3. 女性避开月经期，孕妇不做X光、CT；4. 体检前一天22:00后禁食禁水；5. 携带身份证、既往体检报告。', '体检服务', 'high', '体检,注意事项,准备', 'preset'),
('医疗', '体检报告多久能出来？', '体检报告时间：1. 基础体检（血常规、尿常规、心电图、B超、X光）：3-5个工作日；2. 深度体检（含CT、MRI、胃肠镜）：7-10个工作日；3. 防癌筛查：10-15个工作日。可通过手机APP查询电子报告，也可到医院领取纸质报告。', '体检服务', 'medium', '体检报告,时间', 'preset'),
('医疗', '体检有异常怎么办？', '体检异常处理：1. 轻微异常（如血压、血糖偏高）：调整生活方式，3个月后复查；2. 明显异常：携带体检报告到相应科室就诊；3. 肿瘤标志物升高：不要恐慌，进一步检查确诊；4. 咨询体检中心医生，获取专业建议。', '体检服务', 'high', '体检,异常,复查', 'preset'),
('医疗', '体检项目如何选择？', '体检项目选择建议：1. 基础体检：血常规、尿常规、肝肾功能、心电图、胸部X光、腹部B超（费用300-800元）；2. 深度体检：+肿瘤标志物、CT、胃肠镜（费用1500-3000元）；3. 根据年龄、性别、家族史、职业定制。40岁以上建议加防癌筛查。', '体检服务', 'medium', '体检项目,选择', 'preset'),
('医疗', '多久体检一次合适？', '体检频率建议：1. 18-40岁健康人群：每年1次基础体检；2. 40-60岁或有慢性病：每年1次深度体检；3. 60岁以上：每年1-2次，重点筛查心脑血管、肿瘤；4. 高危人群（吸烟、家族史）：每半年1次。', '体检服务', 'medium', '体检,频率,间隔', 'preset'),
('医疗', '体检可以用医保吗？', '体检医保报销：1. 健康体检：医保不报销，自费；2. 疾病诊断性检查：医保可报销；3. 部分地区医保卡个人账户余额可支付体检费用；4. 退休人员部分地区医保提供免费体检。建议咨询当地医保政策。', '体检服务', 'medium', '体检,医保,报销', 'preset'),
('医疗', '体检当天可以吃早餐吗？', '体检当天进食规定：1. 需空腹项目（抽血、腹部B超、胃镜）：禁食禁水；2. 抽血后可进食早餐（医院通常提供免费早餐）；3. 完成所有空腹项目后再进食；4. 下午体检可在上午少量进食清淡食物。糖尿病患者携带糖果预防低血糖。', '体检服务', 'medium', '体检,早餐,空腹', 'preset'),
('医疗', '体检当天流程是什么？', '体检流程：1. 到达体检中心（早上7:30-8:30）；2. 出示预约信息/身份证，领取体检表；3. 按指引完成各项检查（抽血、B超、心电图、X光等）；4. 完成后交表，告知报告领取时间；5. 全程约2-3小时。建议穿宽松衣物，女士避免穿连衣裙。', '体检服务', 'medium', '体检,流程,当天', 'preset'),
('医疗', '哪些人群需要重点体检？', '重点体检人群：1. 40岁以上中老年人（心脑血管、肿瘤筛查）；2. 长期吸烟饮酒者（肺部、肝脏检查）；3. 家族遗传病史（针对性筛查）；4. 高压职业（公务员、教师、医生）；5. 肥胖、三高人群（代谢综合征检查）；6. 备孕夫妻（孕前检查）。', '体检服务', 'medium', '体检,人群,重点', 'preset');

