-- 零售行业常见问题（50条）
-- ============================================

INSERT INTO industry_question_library (industry, question, answer, category, importance, tags, source_type) VALUES

-- 会员服务类（10条）
('零售', '如何办理会员卡？', '办理会员卡方式：1. 关注我们的微信公众号，点击"会员中心"即可获得电子会员卡；2. 下载官方APP注册账号；3. 到店扫码注册；4. 收银台现场办理实体卡。首次办卡即享新人优惠礼包，生日当月享额外折扣。', '会员服务', 'high', '会员卡,办理,注册', 'preset'),
('零售', '会员有什么优惠？', '会员专属权益：1. 全场商品会员价（9折-9.5折）；2. 每月会员日额外优惠；3. 积分兑换礼品（消费1元=1积分）；4. 生日月专属券；5. 新品优先购买权；6. 免费停车2小时。VIP会员享受更多专属服务。', '会员服务', 'high', '会员,优惠,权益', 'preset'),
('零售', '会员积分如何使用？', '积分使用说明：1. 积分兑换商品：500积分起兑，兑换比例100积分=1元；2. 积分抵现：结账时可用积分抵扣现金（最高抵50%）；3. 积分兑换优惠券；4. 积分有效期1年，到期清零。查询积分：微信公众号、APP、收银台。', '会员服务', 'medium', '会员,积分,兑换', 'preset'),
('零售', '会员卡丢了怎么办？', '会员卡丢失处理：1. 电子会员卡：手机查看微信公众号或APP即可；2. 实体卡丢失：凭手机号和身份证到服务台补办，工本费10元；3. 卡内积分和余额不受影响，自动转入新卡。建议同时开通电子卡，更方便。', '会员服务', 'medium', '会员卡,丢失,补办', 'preset'),
('零售', '会员卡可以给别人用吗？', '会员卡使用规则：1. 会员卡仅限本人使用，享受会员价需出示会员卡；2. 家庭卡可绑定家庭成员（最多3人），共享积分和优惠；3. 转借他人使用可能导致积分异常，影响会员权益。建议为家人单独办理会员卡。', '会员服务', 'low', '会员卡,共用,规则', 'preset'),
('零售', '如何升级VIP会员？', 'VIP会员升级条件：1. 年消费满10000元自动升级；2. 一次性充值会员卡5000元直接升级；3. VIP会员专属权益：全场8.5折、专属客服、优先退换货、生日礼品、免费配送。会员等级每年1月1日更新。', '会员服务', 'medium', '会员,升级,VIP', 'preset'),
('零售', '会员卡余额可以退款吗？', '会员卡余额退款规定：1. 储值金额可退（扣除已使用优惠部分）；2. 赠送金额不可退；3. 退款需携带身份证和会员卡到服务台办理；4. 退款后会员资格取消。注意：使用过优惠的金额按原价补差。建议谨慎充值。', '会员服务', 'medium', '会员,余额,退款', 'preset'),
('零售', '会员日是什么时候？', '会员日活动时间：1. 每月8日、18日、28日为会员日；2. 会员日专享：部分商品额外8折，叠加会员折扣；3. 满赠活动：满200送50元券；4. 积分翻倍；5. 活动详情关注微信公众号或门店海报。黑色星期五、双十一等大促会员享更多优惠。', '会员服务', 'high', '会员日,活动,优惠', 'preset'),
('零售', '会员信息如何修改？', '会员信息修改方法：1. 微信公众号-会员中心-个人信息修改；2. APP-我的-账户设置；3. 到店服务台修改（需身份证验证）。可修改项：手机号、收货地址、生日（仅限一次）。绑定手机号用于接收优惠信息和验证身份。', '会员服务', 'low', '会员,信息,修改', 'preset'),
('零售', '会员有效期是多久？', '会员有效期说明：1. 注册即永久有效，无需年费；2. 2年未消费账户转入休眠状态，积分冻结；3. 再次消费自动激活；4. 积分有效期1年，到期清零；5. VIP会员等级每年根据消费金额重新评定。建议定期登录查看积分和等级。', '会员服务', 'low', '会员,有效期,失效', 'preset'),

-- 退换货服务类（10条）
('零售', '退换货政策是什么？', '退换货政策：1. 7天无理由退货（商品未使用且包装完好）；2. 15天内可换货；3. 需保留购物小票或电子凭证；4. 到店办理或联系客服；5. 特价商品、食品、内衣不支持退换。退款原路返回，3-7个工作日到账。', '售后服务', 'high', '退货,换货,政策', 'preset'),
('零售', '没有发票可以退货吗？', '无发票退货处理：1. 提供购物小票或电子订单号可退货；2. 会员卡消费记录可作为凭证；3. 无任何凭证但商品有防伪标签，可协商退货；4. 建议购物时保留凭证，拍照备份。发票丢失可到服务台查询系统记录。', '售后服务', 'medium', '退货,发票,凭证', 'preset'),
('零售', '商品有质量问题怎么办？', '质量问题处理：1. 7天内：无条件退货退款；2. 15天内：免费换货或维修；3. 1年内：免费维修（人为损坏除外）；4. 需携带商品、发票、保修卡到店。食品质量问题当场处理，假一赔十。联系客服400电话或到服务台投诉。', '售后服务', 'high', '质量问题,投诉,赔偿', 'preset'),
('零售', '网购商品如何退货？', '线上退货流程：1. APP或微信小程序"我的订单"申请退货；2. 填写退货原因，上传照片；3. 等待审核通过（1-2小时）；4. 自行寄回商品或预约上门取件；5. 商品到达仓库，检验后3个工作日内退款。运费：质量问题商家承担,无理由退货买家承担。', '售后服务', 'high', '网购,退货,线上', 'preset'),
('零售', '退货运费谁承担？', '退货运费承担：1. 商品质量问题、发错货：商家承担运费；2. 个人原因退货（不喜欢、买错）：买家承担运费；3. 部分商品包邮，退货时需扣除原运费；4. 到店退货无运费。建议购买前仔细确认尺寸、颜色等信息。', '售后服务', 'medium', '退货,运费,承担', 'preset'),
('零售', '换货需要补差价吗？', '换货差价处理：1. 同等价位商品换货：不补差价；2. 换成更贵商品：补差价；3. 换成更便宜商品：退差价；4. 换货时商品已降价：按现价计算；5. 换货时商品已涨价：按原价计算（购买后15天内）。一次换货机会，换货后不可再退。', '售后服务', 'medium', '换货,差价,规则', 'preset'),
('零售', '赠品需要退回吗？', '赠品退货规定：1. 退货时赠品需一并退回；2. 赠品已使用或丢失，需按赠品价值扣款；3. 满赠活动退货后不满足满赠条件，赠品需退回或补差价；4. 会员日额外赠品需退回。建议退货前确认赠品是否完整。', '售后服务', 'medium', '退货,赠品,满赠', 'preset'),
('零售', '促销商品可以退换吗？', '促销商品退换规定：1. 非质量问题不支持退换；2. 质量问题可退换；3. 特价、清仓、打折商品售出不退不换（门店会标注）；4. 会员价、折扣价商品支持正常退换。建议购买前看清标签和说明。', '售后服务', 'high', '促销,特价,退换', 'preset'),
('零售', '退款多久到账？', '退款到账时间：1. 现金购买：当场退现金；2. 刷卡购买：3-7个工作日退回银行卡；3. 微信/支付宝支付：1-3个工作日原路退回；4. 会员卡支付：即时退回卡内余额。具体时间依银行处理速度而定，可联系客服查询退款进度。', '售后服务', 'medium', '退款,到账,时间', 'preset'),
('零售', '试穿试用过可以退货吗？', '试穿试用退货规定：1. 试穿试用不影响二次销售可退货（商品完好、吊牌未剪、不影响美观）；2. 内衣、食品、化妆品试用后不可退；3. 电子产品开封激活后仅质量问题可退；4. 家具家电送装后不可无理由退。建议购买前仔细试用。', '售后服务', 'medium', '退货,试穿,试用', 'preset'),

-- 配送服务类（10条）
('零售', '配送范围是哪些区域？', '配送范围：1. 市区：全覆盖（三环内）；2. 郊区：部分区域（需咨询客服）；3. 配送费：订单满99元免费配送，不满99元收取10元配送费；4. 偏远地区配送费按实际情况收取。下单前可输入地址查看是否在配送范围内。', '配送服务', 'medium', '配送,范围,区域', 'preset'),
('零售', '配送需要多久？', '配送时效：1. 市区：当天下单次日送达；2. 郊区：2-3天送达；3. 大件家具家电：预约配送，3-7天；4. 急单加急配送：加收20元，6小时内送达（仅限市区）。节假日配送时间顺延。可在订单详情查看物流进度。', '配送服务', 'high', '配送,时间,时效', 'preset'),
('零售', '可以指定配送时间吗？', '配送时间预约：1. 普通配送：配送员提前1小时电话联系确认时间；2. 预约配送：下单时备注期望送达时间（如"周末10:00-12:00"），尽量安排但不保证；3. 大件配送：提前1天电话预约具体时间。建议留准确电话保持畅通。', '配送服务', 'medium', '配送,时间,预约', 'preset'),
('零售', '配送费怎么算？', '配送费标准：1. 订单满99元：免费配送；2. 订单不满99元：配送费10元；3. 大件商品（家具家电）：根据重量和距离30-100元；4. 会员免费配送门槛降低至59元；5. VIP会员全免配送费。满减活动可叠加免费配送。', '配送服务', 'medium', '配送,费用,收费', 'preset'),
('零售', '配送时没人收货怎么办？', '无人收货处理：1. 配送员提前电话联系，无人接听会留言；2. 可联系配送员改约时间；3. 可请邻居或物业代收（需本人同意）；4. 放置代收点（需自行取件）；5. 二次配送可能加收费用。建议预留备用联系人电话。', '配送服务', 'low', '配送,无人,代收', 'preset'),
('零售', '配送可以送货上门吗？', '送货上门服务：1. 普通配送：送至小区大门或楼下；2. 送货上楼：加收楼层费（3楼以上，每层5元）；3. 大件配送：包含上楼安装服务；4. VIP会员免费送货上门。老旧小区无电梯建议选择送货上楼服务。', '配送服务', 'medium', '配送,上门,楼层费', 'preset'),
('零售', '商品破损谁负责？', '商品破损责任：1. 配送途中破损：配送员当场拍照，联系客服退换货或退款，运费和商品损失由商家承担；2. 签收后发现破损：拍照保存，24小时内联系客服；3. 贵重物品建议当面验货。易碎品有防震包装，仍破损可申请赔偿。', '配送服务', 'high', '配送,破损,责任', 'preset'),
('零售', '可以当面验货吗？', '当面验货规定：1. 支持当面拆箱验货，确认无误再签收；2. 验货内容：商品数量、外观完整性、是否破损；3. 不支持试用后再签收（耽误配送效率）；4. 贵重商品、易碎品建议当面验货。发现问题当场拒收并联系客服处理。', '配送服务', 'medium', '配送,验货,拆箱', 'preset'),
('零售', '如何修改配送地址？', '修改配送地址：1. 订单未发货：APP或联系客服修改，免费；2. 订单已发货：联系配送员或客服，可能加收费用；3. 同城可改地址，跨城无法修改；4. 大件商品发货后无法修改。建议下单前仔细核对地址。', '配送服务', 'medium', '配送,地址,修改', 'preset'),
('零售', '配送员态度不好怎么投诉？', '配送服务投诉：1. 拨打客服电话400-XXX-XXXX；2. APP在线客服投诉；3. 提供订单号、配送员信息、问题描述；4. 核实后对配送员进行处罚，给客户补偿（优惠券或积分）；5. 严重违规配送员将被开除。我们重视每一条投诉。', '配送服务', 'medium', '配送,投诉,服务', 'preset'),

-- 支付方式类（10条）
('零售', '支持哪些支付方式？', '支付方式：1. 现金；2. 银行卡刷卡；3. 微信支付；4. 支付宝支付；5. 云闪付；6. 会员卡余额；7. 分期付款（花呗、信用卡分期）；8. 企业对公转账（需提前申请）。推荐微信/支付宝，支付快捷且享优惠。', '支付方式', 'high', '支付,方式,支付宝', 'preset'),
('零售', '可以分期付款吗？', '分期付款方式：1. 花呗分期：3/6/12期免息（商家贴息）；2. 信用卡分期：3/6/12期，手续费0.6%/月；3. 门店分期：满3000元可申请，需提供身份证和信用卡；4. 分期后不支持退货，仅支持换货。分期前请确认还款能力。', '支付方式', 'medium', '支付,分期,花呗', 'preset'),
('零售', '可以用多种方式一起付款吗？', '组合支付：1. 支持多种支付方式组合（如会员卡余额+微信）；2. 可使用优惠券+积分+现金组合；3. 不支持多张信用卡组合支付；4. 组合支付结账时告知收银员即可。退款时按原支付方式原路返回。', '支付方式', 'medium', '支付,组合,混合', 'preset'),
('零售', '支付失败怎么办？', '支付失败原因及解决：1. 余额不足：充值后重试；2. 网络问题：切换网络或使用其他支付方式；3. 银行卡限额：联系银行提高限额或分笔支付；4. 系统维护：稍后重试或更换支付方式；5. 支付超时：重新下单。多次失败联系客服。', '支付方式', 'medium', '支付,失败,问题', 'preset'),
('零售', '支付后没收到商品怎么办？', '支付后处理流程：1. 线上购买：查看订单状态，等待发货；2. 线下购买：支付成功凭小票或订单号到提货处取货；3. 支付成功但未收到短信：查看APP订单或联系客服；4. 支付成功但订单未生成：保留支付凭证联系客服，24小时内处理。', '支付方式', 'medium', '支付,未收货,订单', 'preset'),
('零售', '可以用别人的账号支付吗？', '支付账号使用：1. 可以用他人账号帮忙付款（朋友代付、家人代付）；2. 会员优惠仅限本人账号享受；3. 积分累计到实际支付账号；4. 发票抬头以支付账号为准；5. 企业采购建议使用对公账户，可开专票。代付时确认金额无误。', '支付方式', 'low', '支付,代付,账号', 'preset'),
('零售', '付款后可以换支付方式吗？', '更换支付方式：1. 已支付订单无法更换支付方式；2. 如需更换：先申请退款，到账后重新下单用新支付方式；3. 会员卡支付可即时退回卡内；4. 银行卡/微信/支付宝支付退款需3-7天。建议支付前确认支付方式。', '支付方式', 'low', '支付,更换,方式', 'preset'),
('零售', '发票可以开吗？', '发票开具：1. 支持开具电子发票和纸质发票（个人/企业）；2. 开票方式：结账时提供税号和抬头，或扫码自助开票；3. 电子发票当场发送到邮箱/手机；4. 纸质发票7天内到服务台领取或邮寄（邮费自理）；5. 发票内容：商品明细或类目。', '支付方式', 'medium', '发票,开票,税号', 'preset'),
('零售', '支付有优惠吗？', '支付优惠活动：1. 微信支付随机立减5-50元；2. 支付宝支付满100减10元；3. 云闪付满200减20元；4. 特定银行信用卡满减活动；5. 活动时间和规则详见APP或门店海报。支付优惠可与会员折扣、优惠券叠加。', '支付方式', 'high', '支付,优惠,满减', 'preset'),
('零售', '可以货到付款吗？', '货到付款服务：1. 部分区域支持货到付款（市区部分区域）；2. 货到付款加收10元手续费；3. 货到付款仅支持现金，不支持刷卡；4. 贵重商品不支持货到付款；5. 货到付款订单需电话确认。建议在线支付，更快捷优惠。', '支付方式', 'low', '支付,货到付款', 'preset'),

-- 商品咨询类（10条）
('零售', '商品是正品吗？', '正品保障：1. 所有商品均从品牌官方/授权经销商采购，100%正品保证；2. 提供品牌授权书和进货凭证；3. 支持专柜验货、官网防伪查询；4. 假一赔十承诺；5. 可开具正规发票。疑似假货可联系客服举证，核实后严肃处理并赔偿。', '商品咨询', 'high', '正品,真假,品质', 'preset'),
('零售', '商品生产日期是什么时候？', '商品日期说明：1. 食品类：保证至少2/3保质期以上（例如保质期12个月的食品，到手至少剩8个月）；2. 化妆品：保证1年以上保质期；3. 临期商品会明确标注，特价销售；4. 下单备注"要最新日期"可优先发新货（不保证绝对最新）。', '商品咨询', 'medium', '商品,日期,保质期', 'preset'),
('零售', '商品缺货多久能到？', '缺货补货时间：1. 热门商品：3-7天补货；2. 普通商品：7-15天；3. 进口商品：15-30天；4. 季节性商品：换季后补货。缺货商品可预定，到货优先通知。建议关注公众号"到货提醒"，第一时间获取补货信息。', '商品咨询', 'medium', '商品,缺货,补货', 'preset'),
('零售', '尺码不合适怎么办？', '尺码问题处理：1. 购买前咨询客服或查看尺码表；2. 试穿后不合适15天内免费换货（吊牌未剪）；3. 部分品牌支持免费上门取换；4. 商品详情页有真人试穿效果和尺码建议。建议拍照或量体后咨询客服推荐合适尺码。', '商品咨询', 'high', '商品,尺码,大小', 'preset'),
('零售', '商品和图片不一样怎么办？', '商品描述不符处理：1. 颜色色差：显示器不同可能有轻微色差，实物颜色以商品详情描述为准，不属于质量问题；2. 款式、材质不符：属于质量问题，支持退换货；3. 虚假宣传：向客服投诉，核实后严肃处理并补偿。', '商品咨询', 'medium', '商品,图片,色差', 'preset'),
('零售', '可以试穿试用吗？', '试穿试用服务：1. 服装鞋帽：试衣间免费试穿（不剪吊牌）；2. 化妆品：提供小样试用（部分品牌）；3. 电子产品：可体验基础功能，不可激活；4. 家具：展厅样品可体验；5. 线上购买：收到商品可试穿，不满意退货（保持商品完好）。', '商品咨询', 'medium', '商品,试穿,试用', 'preset'),
('零售', '新品什么时候上架？', '新品上架时间：1. 服装鞋帽：每周三、周六上新；2. 食品：每周一上新；3. 家电数码：新品发布当天或次日上架；4. 化妆品：每月1日上新。新品首发会员享优先购买权，关注公众号"新品速递"获取上新提醒。', '商品咨询', 'medium', '商品,新品,上架', 'preset'),
('零售', '促销商品质量有保证吗？', '促销商品说明：1. 正常促销（满减、打折）：质量与正价商品完全一致；2. 清仓特价：过季商品或库存商品，质量无问题但款式老旧；3. 临期食品：明确标注日期，质量合格；4. 所有商品均享受质量保障，出现问题可售后。', '商品咨询', 'medium', '促销,质量,特价', 'preset'),
('零售', '商品包装可以定制吗？', '包装服务：1. 免费包装：普通包装袋；2. 礼品包装：精美礼盒，收费10-50元（根据商品大小）；3. 定制包装：企业采购可定制LOGO包装，起订量100件；4. 节日包装：春节、情人节、圣诞节提供主题包装。下单时备注或到服务台咨询。', '商品咨询', 'low', '商品,包装,礼盒', 'preset'),
('零售', '库存还有多少？', '库存查询：1. 线上商品：商品详情页显示库存（仅剩XX件）；2. 线下商品：到店咨询导购或客服电话查询；3. 库存实时更新，下单前请确认；4. 库存不足时系统会提示；5. 热门商品建议提前预定。VIP会员可预留商品48小时。', '商品咨询', 'low', '商品,库存,数量', 'preset');

-- ============================================
-- IT/软件行业常见问题（50条）
-- ============================================

INSERT INTO industry_question_library (industry, question, answer, category, importance, tags, source_type) VALUES

-- 账户管理类（10条）
('IT/软件', '如何注册账号？', '注册账号步骤：1. 打开APP或官网，点击"注册"；2. 输入手机号/邮箱，获取验证码；3. 设置密码（8-16位，包含字母+数字）；4. 完善个人信息（可选）；5. 勾选用户协议，完成注册。注册即赠送新人优惠券。支持微信、QQ、支付宝第三方账号快捷注册。', '账户管理', 'high', '注册,账号,新用户', 'preset'),
('IT/软件', '如何重置密码？', '重置密码方法：1. 点击登录页面的"忘记密码"；2. 输入注册邮箱或手机号；3. 接收验证码并验证；4. 设置新密码；5. 完成重置，使用新密码登录。如无法收到验证码，检查短信拦截或联系在线客服。建议定期更换密码，保障账户安全。', '账户管理', 'high', '密码,重置,忘记密码', 'preset'),
('IT/软件', '如何修改个人信息？', '修改个人信息步骤：1. 登录账号，进入"个人中心"；2. 点击"编辑资料"；3. 修改昵称、头像、性别、生日、地址等；4. 保存更改。手机号和邮箱修改需验证身份（发送验证码）。实名认证信息提交后不可修改。', '账户管理', 'medium', '个人信息,修改,资料', 'preset'),
('IT/软件', '如何注销账号？', '注销账号流程：1. 进入"设置-账户与安全-注销账号"；2. 阅读注销须知（数据将永久删除）；3. 验证身份（短信验证码或密码）；4. 确认注销；5. 7天冷静期后正式注销。注销前请备份重要数据，提现余额，注销后无法找回。', '账户管理', 'medium', '注销,删除账号,账户', 'preset'),
('IT/软件', '账号被盗怎么办？', '账号被盗应对措施：1. 立即修改密码；2. 检查绑定手机号和邮箱是否被篡改，及时改回；3. 查看登录记录和消费记录，发现异常交易联系客服冻结账户；4. 开启二次验证（短信验证码、指纹、人脸识别）；5. 联系客服申诉找回账号。', '账户管理', 'high', '账号,被盗,安全', 'preset'),
('IT/软件', '如何绑定手机号？', '绑定手机号方法：1. 进入"设置-账户与安全-手机号"；2. 输入手机号；3. 获取并输入验证码；4. 绑定成功。绑定手机号用途：找回密码、接收验证码、账户安全保护。更换手机号需验证原手机号或通过客服人工审核。', '账户管理', 'medium', '手机号,绑定,验证', 'preset'),
('IT/软件', '支持第三方账号登录吗？', '第三方登录方式：1. 微信登录；2. QQ登录；3. 支付宝登录；4. Apple ID登录（iOS）；5. 谷歌账号登录（国际版）。首次使用第三方登录会自动创建账号。建议绑定手机号，方便账号找回和多设备登录。', '账户管理', 'medium', '第三方登录,微信,QQ', 'preset'),
('IT/软件', '如何开启双重验证？', '开启双重验证（2FA）：1. 进入"设置-账户与安全-登录保护"；2. 选择验证方式（短信验证码、谷歌验证器、邮箱验证码）；3. 按提示完成设置；4. 启用后每次登录需额外验证。推荐开启双重验证，大幅提升账户安全性，防止盗号。', '账户管理', 'medium', '双重验证,安全,2FA', 'preset'),
('IT/软件', '可以多设备登录吗？', '多设备登录说明：1. 支持同一账号在多个设备登录（手机、电脑、平板）；2. 最多同时登录5台设备；3. 超过限制自动踢出最早登录的设备；4. 可在"账户设置-设备管理"查看和管理所有登录设备；5. 发现陌生设备可远程下线。', '账户管理', 'medium', '多设备,登录,同时在线', 'preset'),
('IT/软件', '登录超时怎么办？', '登录超时处理：1. 7天未登录自动退出，重新登录即可；2. 异地登录或更换设备需验证身份；3. 长时间未操作（30分钟）自动退出，重新登录即可；4. 保持登录状态：勾选"记住我"或"自动登录"。建议定期登录，避免账号被系统回收（1年未登录）。', '账户管理', 'low', '登录,超时,掉线', 'preset');

-- 下单与支付（10条）
('IT/软件', '如何下单购买？', '下单购买流程：1. 浏览商品/服务，点击"立即购买"或"加入购物车"；2. 选择规格、数量，确认价格；3. 填写收货地址和联系方式；4. 选择支付方式，使用优惠券；5. 确认订单信息，提交订单；6. 完成支付。支付成功后订单自动生效。', '订单支付', 'high', '下单,购买,订单', 'preset'),
('IT/软件', '支持哪些支付方式？', '支付方式：1. 微信支付；2. 支付宝；3. 银行卡支付（储蓄卡、信用卡）；4. PayPal（国际版）；5. 余额支付（账户充值余额）；6. 花呗分期；7. 对公转账（企业用户）；8. 虚拟货币（部分平台）。推荐微信/支付宝，方便快捷且支持退款。', '订单支付', 'high', '支付,付款,方式', 'preset'),
('IT/软件', '支付失败怎么办？', '支付失败原因及解决：1. 余额不足：充值后重试；2. 银行卡被冻结：联系银行解冻；3. 网络超时：检查网络连接，重新支付；4. 支付限额：更换支付方式或分笔支付；5. 银行风控：联系银行确认安全后重试；6. 系统维护：稍后重试。多次失败联系客服。', '订单支付', 'medium', '支付,失败,错误', 'preset'),
('IT/软件', '订单可以取消吗？', '订单取消规则：1. 未支付订单：直接取消，无影响；2. 已支付未发货订单：申请取消，退款3-7个工作日；3. 已发货订单：无法取消，收货后可退货；4. 虚拟商品（会员、课程）：支付后不可取消；5. 频繁取消订单可能影响信用。', '订单支付', 'medium', '订单,取消,退单', 'preset'),
('IT/软件', '订单多久发货？', '发货时间：1. 实体商品：24-48小时内发货（节假日顺延）；2. 虚拟商品（会员、软件）：支付后即时开通；3. 预售商品：预售结束后7-15天发货；4. 定制商品：15-30天发货。可在"订单详情"查看物流进度。紧急需求可联系客服加急。', '订单支付', 'medium', '订单,发货,时间', 'preset'),
('IT/软件', '如何查看订单状态？', '查看订单状态：1. 进入"我的订单"；2. 查看订单列表（待支付、待发货、待收货、已完成）；3. 点击订单查看详情（商品信息、物流进度、发票）；4. 订单状态变更会短信/APP推送通知。订单异常（超时未发货）可联系客服催单或退款。', '订单支付', 'medium', '订单,状态,查询', 'preset'),
('IT/软件', '订单可以修改吗？', '订单修改规则：1. 未支付订单：可修改商品、数量、地址；2. 已支付未发货订单：联系客服修改地址，可能无法修改商品；3. 已发货订单：无法修改，拒收后重新下单；4. 虚拟商品订单：不支持修改。建议下单前仔细核对信息。', '订单支付', 'medium', '订单,修改,变更', 'preset'),
('IT/软件', '发票如何开具？', '开具发票流程：1. 下单时勾选"需要发票"；2. 选择发票类型（电子普票、纸质普票、增值税专票）；3. 填写发票抬头、税号、邮箱；4. 提交订单；5. 电子发票支付后1-3天发送到邮箱；6. 纸质发票随货寄出或单独邮寄。企业采购建议开增值税专票。', '订单支付', 'medium', '发票,开票,税票', 'preset'),
('IT/软件', '优惠券如何使用？', '优惠券使用方法：1. 领取优惠券（首页、活动页、会员中心）；2. 下单时选择可用优惠券；3. 系统自动抵扣金额；4. 提交订单并支付。注意：优惠券有使用门槛（满减）、有效期、适用范围（部分商品不可用）。优惠券不可叠加（除特殊活动）。', '订单支付', 'high', '优惠券,折扣,代金券', 'preset'),
('IT/软件', '余额充值可以退款吗？', '余额退款规定：1. 充值未使用：可全额退款，3-7个工作日到账；2. 部分使用：剩余部分可退；3. 充值赠送部分不可退；4. 退款需实名认证并提供充值凭证；5. 频繁充值退款可能被限制。建议按需充值，避免资金闲置。', '订单支付', 'medium', '充值,余额,退款', 'preset');

-- 技术支持类（10条）
('IT/软件', '软件如何安装？', '软件安装步骤：1. 访问官网下载最新版安装包（或应用商店下载）；2. 双击安装包，按提示操作（同意协议、选择路径）；3. 等待安装完成；4. 运行软件，注册/登录账号；5. 根据引导完成初始化设置。Mac用户拖拽到Applications文件夹。安装问题查看FAQ或联系客服。', '技术支持', 'high', '安装,下载,软件', 'preset'),
('IT/软件', '软件如何升级？', '软件升级方法：1. 打开软件，点击"检查更新"；2. 确认升级版本信息和更新日志；3. 点击"立即升级"；4. 等待下载安装完成，重启软件；5. 建议开启"自动升级"功能。升级前建议备份数据。重大版本升级可能需要重新激活。', '技术支持', 'medium', '升级,更新,版本', 'preset'),
('IT/软件', '软件闪退怎么办？', '软件闪退解决方案：1. 重启软件；2. 更新到最新版本；3. 检查系统兼容性（Win7及以上，MacOS 10.12及以上）；4. 清理缓存和临时文件；5. 卸载重装；6. 关闭冲突软件（杀毒软件、防火墙）；7. 联系客服提供日志文件（设置-帮助-导出日志）。', '技术支持', 'high', '闪退,崩溃,软件', 'preset'),
('IT/软件', '如何备份数据？', '数据备份方法：1. 自动备份：设置-备份-开启自动备份（每天/每周），数据保存到云端；2. 手动备份：文件-导出数据-选择格式（JSON/CSV/SQL）-保存到本地；3. 云同步：登录账号，数据自动同步到云端；4. 建议重要数据定期备份到外部硬盘或云盘。', '技术支持', 'high', '备份,数据,云同步', 'preset'),
('IT/软件', '如何恢复数据？', '数据恢复方法：1. 云端恢复：登录账号，点击"恢复数据"，选择备份时间点；2. 本地恢复：文件-导入数据-选择备份文件；3. 误删恢复：回收站找回（保留30天）；4. 系统崩溃恢复：重装软件后登录账号自动同步。无法恢复联系客服技术支持。', '技术支持', 'high', '恢复,数据,找回', 'preset'),
('IT/软件', '软件运行慢怎么办？', '软件运行慢优化：1. 清理缓存（设置-清理缓存）；2. 关闭不必要的后台进程；3. 升级硬件（加内存、换SSD）；4. 降低软件设置（关闭特效、降低分辨率）；5. 卸载重装；6. 检查病毒（用杀毒软件扫描）；7. 系统配置不足考虑升级到专业版（轻量化）。', '技术支持', 'medium', '卡顿,慢,优化', 'preset'),
('IT/软件', '忘记激活码怎么办？', '激活码找回方法：1. 查看购买确认邮件（邮件中包含激活码）；2. 登录账号-我的订单-查看订单详情；3. 联系客服提供订单号或购买邮箱找回；4. 激活码一旦使用绑定设备，更换设备需解绑（设置-设备管理）。建议截图保存激活码。', '技术支持', 'medium', '激活码,序列号,找回', 'preset'),
('IT/软件', '软件支持哪些系统？', '系统兼容性：Windows版：Win7/8/10/11（64位）；Mac版：MacOS 10.12及以上；Linux版：Ubuntu 18.04及以上；移动端：iOS 12及以上，Android 8.0及以上；Web版：Chrome、Firefox、Safari、Edge最新版。不同系统功能略有差异，详见官网说明。', '技术支持', 'medium', '系统,兼容,支持', 'preset'),
('IT/软件', '如何联系技术支持？', '技术支持渠道：1. 在线客服：官网/APP点击右下角客服图标（工作日9:00-18:00）；2. 邮件支持：support@example.com（24小时内回复）；3. 电话支持：400-XXX-XXXX（工作日9:00-18:00）；4. 工单系统：提交详细问题描述和截图；5. 社区论坛：官方论坛提问。', '技术支持', 'high', '客服,支持,联系', 'preset'),
('IT/软件', '软件可以离线使用吗？', '离线使用说明：1. 基础功能支持离线使用（编辑、查看）；2. 高级功能需要联网（云同步、AI功能、插件）；3. 首次使用需联网激活和下载资源；4. 离线期间数据保存本地，联网后自动同步；5. 建议定期联网以获取更新和备份数据。离线模式详见设置。', '技术支持', 'medium', '离线,网络,使用', 'preset');

-- 会员服务类（10条）
('IT/软件', '会员有什么特权？', '会员特权：1. 功能解锁：使用全部高级功能（无限制）；2. 云存储：100GB云存储空间（免费版仅5GB）；3. 无广告：纯净使用体验；4. 优先支持：客服优先响应，专属技术支持；5. 批量操作：批量导入导出、批量处理；6. 多设备同步：最多10台设备；7. 优惠折扣：续费8折，新功能优先体验。', '会员服务', 'high', '会员,VIP,特权', 'preset'),
('IT/软件', '会员如何购买？', '购买会员流程：1. 进入"会员中心"或"设置-升级会员"；2. 选择套餐（月卡/季卡/年卡/永久）；3. 选择支付方式（微信/支付宝/银行卡）；4. 完成支付；5. 会员立即生效。推荐购买年卡，相当于10个月的价格。团队可购买企业版，享受批量优惠。', '会员服务', 'high', '会员,购买,开通', 'preset'),
('IT/软件', '会员价格是多少？', '会员价格：月卡：29元/月；季卡：68元/季（折合22.7元/月）；年卡：199元/年（折合16.6元/月）；永久会员：999元（终身使用）。学生认证享5折优惠。企业版：按人数定价，10人起，200元/人/年。新用户首月9.9元体验。活动期间额外优惠。', '会员服务', 'high', '会员,价格,费用', 'preset'),
('IT/软件', '会员可以退款吗？', '会员退款政策：1. 开通7天内未使用可全额退款；2. 已使用按天数比例扣除，剩余退款；3. 活动折扣购买不支持退款（特别说明除外）；4. 永久会员不支持退款；5. 退款联系客服提供订单号，审核后3-7个工作日到账。建议先试用免费版确认需求。', '会员服务', 'medium', '会员,退款,取消', 'preset'),
('IT/软件', '会员可以转让吗？', '会员转让规定：1. 会员账号不支持转让给他人；2. 会员绑定账号和设备，更换设备需解绑；3. 企业版可转移到新员工（联系客服）；4. 特殊情况（账号被盗）可申诉找回。建议不要共享账号，影响数据安全，违规可能被封号。', '会员服务', 'low', '会员,转让,共享', 'preset'),
('IT/软件', '会员到期后怎么办？', '会员到期处理：1. 会员到期后自动转为免费版，部分功能受限；2. 数据不会丢失，保留在云端（可继续查看）；3. 续费后立即恢复会员权益；4. 建议开启自动续费，享受续费优惠（9折）且避免中断。到期前7天会邮件和短信提醒。', '会员服务', 'medium', '会员,到期,续费', 'preset'),
('IT/软件', '学生可以享受优惠吗？', '学生优惠政策：1. 学生认证（上传学生证）可享5折优惠；2. 学生会员：年卡99元（原价199元）；3. 认证有效期1年，需每年重新认证；4. 毕业后自动转为普通会员价格。教师同样享受学生优惠。上传证件信息将加密保存，仅用于认证。', '会员服务', 'medium', '学生,优惠,折扣', 'preset'),
('IT/软件', '企业版和个人版有什么区别？', '企业版vs个人版：个人版：单用户使用，云存储100GB，最多10台设备；企业版：多用户管理、统一账单、数据集中管理、500GB+云存储、专属客服、定制功能开发、数据安全保障、支持私有部署。企业版适合团队协作，价格更优惠。', '会员服务', 'medium', '企业版,个人版,区别', 'preset'),
('IT/软件', '可以试用会员功能吗？', '会员试用：1. 新用户注册赠送7天VIP体验；2. 邀请好友注册双方各得3天VIP；3. 参加活动获得试用资格；4. 部分功能提供限次免费使用（如AI功能每日3次）。试用期满可购买会员继续使用。试用期数据保留，升级后无缝衔接。', '会员服务', 'medium', '会员,试用,体验', 'preset'),
('IT/软件', '自动续费如何取消？', '取消自动续费：1. 进入"设置-会员中心-自动续费管理"；2. 点击"取消自动续费"；3. 确认取消；4. 取消后本期会员仍有效至到期日。也可通过支付平台取消（微信：我-支付-自动扣费；支付宝：我的-设置-支付设置-免密支付/自动扣款）。取消后可随时重新开启。', '会员服务', 'medium', '自动续费,取消,扣费', 'preset');

-- 隐私与安全类（10条）
('IT/软件', '如何保护账户安全？', '账户安全措施：1. 设置复杂密码（字母+数字+符号，8位以上）；2. 开启双重验证（短信验证码、谷歌验证器）；3. 绑定手机号和邮箱；4. 不要共享账号密码；5. 定期更换密码；6. 不在公共电脑登录，登录后记得退出；7. 开启登录通知，及时发现异常登录。', '隐私安全', 'high', '安全,账户,保护', 'preset'),
('IT/软件', '隐私政策是什么？', '隐私政策要点：1. 收集信息：注册信息（手机号、邮箱）、使用数据（操作日志）、设备信息；2. 用途：提供服务、改进产品、发送通知；3. 不会出售个人信息给第三方；4. 采用加密技术保护数据安全；5. 用户可随时查看、修改、删除个人信息。详见官网《隐私政策》。', '隐私安全', 'high', '隐私,政策,数据', 'preset'),
('IT/软件', '数据会被泄露吗？', '数据安全保障：1. 数据传输加密（SSL/TLS）；2. 数据存储加密（AES-256）；3. 服务器部署在国内知名云服务商（阿里云/腾讯云）；4. 定期安全审计和漏洞修复；5. 严格的员工数据访问权限管理；6. 通过ISO 27001信息安全认证。我们高度重视用户隐私和数据安全。', '隐私安全', 'high', '数据,泄露,安全', 'preset'),
('IT/软件', '可以导出我的数据吗？', '数据导出方法：1. 进入"设置-数据管理-导出数据"；2. 选择导出内容（全部数据/部分数据）；3. 选择格式（JSON/CSV/PDF）；4. 点击导出，等待打包完成；5. 下载压缩包到本地。导出的数据包含所有个人信息和使用数据。用户拥有数据完全所有权。', '隐私安全', 'medium', '数据,导出,下载', 'preset'),
('IT/软件', '如何删除我的数据？', '删除数据方法：1. 删除部分数据：在对应功能内删除（如删除文章、删除记录）；2. 删除全部数据：注销账号时选择"删除所有数据"；3. 数据删除后无法恢复；4. 部分数据因法律要求需保留一定时间（如订单、发票信息）。建议删除前先导出备份。', '隐私安全', 'medium', '删除,数据,清空', 'preset'),
('IT/软件', 'Cookies是什么？', 'Cookies说明：1. Cookies是网站存储在您设备上的小文件；2. 用途：记住登录状态、保存偏好设置、统计访问数据、提升用户体验；3. 可在浏览器设置中禁用Cookies，但可能影响使用；4. 我们使用的Cookies不包含敏感个人信息。可在官网查看《Cookies政策》。', '隐私安全', 'low', 'Cookies,隐私', 'preset'),
('IT/软件', '如何修改隐私设置？', '隐私设置修改：1. 进入"设置-隐私设置"；2. 设置信息可见范围（公开/好友可见/仅自己）；3. 关闭不需要的权限（位置、通讯录、相机）；4. 设置谁可以联系我、谁可以查看我的动态；5. 屏蔽用户；6. 保存设置。建议定期检查隐私设置，保护个人信息。', '隐私安全', 'medium', '隐私,设置,权限', 'preset'),
('IT/软件', '个人信息会被用于广告吗？', '广告政策：1. 会员用户无广告；2. 免费用户会看到广告，但不使用敏感个人信息（如聊天内容）定向投放；3. 仅使用匿名统计数据（如年龄段、地区）优化广告；4. 不会出售个人信息给广告商；5. 可选择关闭个性化广告（设置-隐私-广告设置）。', '隐私安全', 'medium', '广告,隐私,定向', 'preset'),
('IT/软件', '如何举报违规内容？', '举报违规内容：1. 点击内容右上角"三个点-举报"；2. 选择举报原因（色情、暴力、诈骗、侵权等）；3. 提供详细说明和截图证据；4. 提交举报；5. 系统审核（24小时内），违规内容将被删除，严重者封号。感谢您的监督，共同维护绿色网络环境。', '隐私安全', 'medium', '举报,违规,投诉', 'preset'),
('IT/软件', '什么是GDPR合规？', 'GDPR合规说明：GDPR（通用数据保护条例）是欧盟数据保护法规。我们的合规措施：1. 明确告知数据收集用途并征得同意；2. 用户有权访问、修改、删除个人数据；3. 数据泄露24小时内通知用户；4. 指定数据保护官（DPO）；5. 数据处理合法、透明、最小化。欧盟用户可联系DPO@example.com。', '隐私安全', 'low', 'GDPR,合规,欧盟', 'preset');

