CREATE TABLE `enterprise_knowledge_base` (
  `id` int NOT NULL AUTO_INCREMENT,
  `title` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '知识标题/问题',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '知识内容/答案',
  `keywords` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '关键词(逗号分隔)',
  `priority` int NOT NULL DEFAULT '0' COMMENT '优先级(0-100)',
  `summary` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT 'AI生成的摘要',
  `scene_category` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '场景分类:首次沟通|产品介绍|价格咨询|异议处理|售后服务|其他',
  `product_category` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '产品分类:产品ID或分类名',
  `customer_type` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '客户类型:新客|老客|高意向|中意向|低意向',
  `question_type` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '问题类型:产品问题|价格问题|服务问题|效果问题|师资问题|其他',
  `relevance_score` decimal(5,2) NOT NULL DEFAULT '0.00' COMMENT 'AI相关度评分(0-100)',
  `quality_score` decimal(5,2) NOT NULL DEFAULT '0.00' COMMENT '内容质量评分(0-100)',
  `usage_count` int NOT NULL DEFAULT '0' COMMENT '使用次数',
  `positive_feedback_count` int NOT NULL DEFAULT '0' COMMENT '正反馈次数',
  `negative_feedback_count` int NOT NULL DEFAULT '0' COMMENT '负反馈次数',
  `last_used_time` datetime DEFAULT NULL COMMENT '最后使用时间',
  `source_type` enum('manual','ai_mining','industry_recommend','file_import') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'manual' COMMENT '来源类型',
  `source_id` int DEFAULT NULL COMMENT '来源记录ID(如chat_record_id)',
  `creator_id` int DEFAULT NULL COMMENT '创建人ID',
  `related_product_ids` json DEFAULT NULL COMMENT '关联的产品ID列表',
  `create_time` datetime(6) NOT NULL DEFAULT CURRENT_TIMESTAMP(6) COMMENT '创建时间',
  `update_time` datetime(6) NOT NULL DEFAULT CURRENT_TIMESTAMP(6) ON UPDATE CURRENT_TIMESTAMP(6) COMMENT '更新时间',
  `status` enum('active','inactive','pending_review','auto_disabled') CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'active' COMMENT '状态',
  PRIMARY KEY (`id`),
  KEY `idx_source_type` (`source_type`),
  KEY `idx_status` (`status`),
  KEY `idx_question_type` (`question_type`),
  KEY `idx_customer_type` (`customer_type`),
  KEY `idx_product_category` (`product_category`),
  KEY `idx_scene_category` (`scene_category`)
) ENGINE=InnoDB AUTO_INCREMENT=31 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
